var mws = require('./package.json').dependencies;
function exports(exportsObj) {
  Object.keys(mws).forEach(function(name) {
    var str = mws[name];
    var splits = str.split(' ');

    var mw = require(name);
    var mwName = splits[0];
    exportsObj[mwName] = mw;
    for(var i=2; i<splits.length; ++i) {
      var submwName = splits[i];
      var submw = mw[submwName];
      if (submw instanceof Function) {
        exportsObj[submwName] = submw;
      }
    }
  });
}
module.exports = exports;
exports(module.exports);

